import '../App.css';
import React from 'react';
import ReactDOM from 'react-dom';
 import reimg from '../images/reimg.svg';
import Appdata from '../statedata';

class Register extends React.Component{
    constructor(props){

      super();
    }

    render(){
        return(
        <div className="register-div">
            <form className="input-form-div">
            <h1></h1>
           {inputag}
            <button>continue -></button>
            </form>
             <div className="register-img-panel">
                <img className="register-img" src={reimg}/>
                <div className="register-text">
                <h4>Register an account</h4>
                <p>farm with ease</p>
                <p>farm on the go</p>
                 <p>with mobile</p>

              </div>
            </div>
            </div>
        );
    }
}

const Textbox=(props)=>{
    return(
  <input className={props.class} placeholder={props.caption}/>  
    );
}

const [,,,tags]=Appdata;

const inputag=tags.input_tag.map((n)=>{
    return(<Textbox class="input-tag" caption={n}/>);

})




export default Register;
