import React from 'react';
import ReactDom from 'react-dom';
import '../App.css';
import icon from '../images/icon-logo.svg';
import {Link} from 'react-router-dom';
import Register from './register';

class Login extends React.Component{
    constructor(props){
       super(); 
    }
    render(){
        return(
            <div className="main-login-container">
            <img className="icon" src={icon}/>
                   <div className="row1">
            <p className="T-COLOR n-1 ft">New to Securefarmer?</p>
          <Link activeClassName="active-link" to="/register" src={Register}>  <button className="btn1"> create an account</button></Link>
                    </div>
               <div className="login-container">

            <h2 className="t-1-c ft">Sign in</h2>
              <button className="g-btn"> Sign in with your Gmail</button>
           <p className="si-1">  or sign in with your email</p><hr className="line1"/>
                  <Textinput caption="Email adress" name ="Enter email adress" />
                  <Textinput caption="Password" name  ="password" />
                  < button className="sign1">sign in</button>
                </div>
            </div>
        )
    }

}
const Textinput=(props)=>{
    return(
    <div>
       <b> <p className="c1">{props.caption}</p></b>
   <input className="login-inputs" type="text" placeholder={props.name}></input>
    </div>
    );
}
export default Login;
