import '../App.css';
import React from 'react';
import ReactDOM from 'react-dom';
 import reimg from '../images/reimg.svg';
import Appdata from '../statedata';
 import axios from 'axios';
class Textbox extends React.Component{
    constructor(props){
        super();
        this.state={
          firstname:"",
            email:"",
             surname:"",
              class:"i-btn",
            disabled:"false",
            text:"See how to connect to your farming activities"

        }
    }


    Changefirstname=(e)=>{
       this.setState({firstname:e.target.value})

    }

    Changeemail=(e)=>{
       this.setState({email:e.target.value})

    }

    Changesurname=(e)=>{
       this.setState({surname:e.target.value})

    }
    SendToDatabase=async(e)=>{
          this.setState({class:"i-btn-r"}); 
        e.preventDefault();
       const  data={
                firstname:this.state.firstname,
                  surname:this.state.surname,
                    email:this.state.email
            }
        axios.post('http://localhost:5000/api/signup',{data})
            .then((res)=>{
            console.log(res.data);
            if (res){
                 this.setState({class:"i-btn"});
                this.setState({disabled:"false"})
                this.setState({text:{res}})
            }


        })



    }

    render(){
        return(
            <div>
            <p>   {this.state.text} </p>
         <input onChange={ this.Changefirstname} className="input-tag" placeholder="First-name"/>
             <input onChange={ this.Changesurname} className="input-tag" placeholder=" Surnamee"/>
             <input onChange={this.Changeemail} className="input-tag" placeholder=" E-mail"/>
             <button  onClick={this.SendToDatabase} className={this.state.class}>continue </button>
             </div>
        );
    }
}
export default Textbox;
