import '../App.css';
import React from 'react';
import ReactDOM from 'react-dom';
 import reimg from '../images/reimg.svg';
import Appdata from '../statedata';
import logo from '../images/homelogo.svg';
import {Link} from 'react-router-dom';
import Login from './login';
import icon from '../images/icon-logo.svg';

class Register extends React.Component{
    constructor(props){
        //onFirstname=onFirstname().bind(this);
      super();
        this.state={
            firstname:"",
                surname:"",
                    email:"",

        }
    }

     Firstname=(e)=>{
         this.setState(
             {
                 firstname:e.target.value}  )      

       }

      Surname=(e)=>{
         this.setState(
             {
                 surname:e.target.value}  )      

       }
      Email=(e)=>{
         this.setState(
             {
                 email:e.target.value}  )      

       }

      //input tag codes

     // 


    render(){
        return(
            <div className="register-main-container">

            <img className="icon1" src={icon}/>
                <div className="register-div">

                    <form className="input-form-div">
                           <h2>Set up your farming account</h2>
                           <p>   {signin_text} </p>
                           { Inputag}
                           <button className="i-btn">continue </button>
                           <p> already have an account 
                            <Link activeClassName="active-link" to="/login" src={Login}>
                            <p className="diff">Sign in</p></Link></p>
                     </form>
                    <div className="register-img-panel">
                           <img className="register-img" src={reimg}/>
                     </div>
                 </div>
            </div>
        );
    }
}
const [s_text]=Appdata;
const signin_text=s_text.signin_text;





const Textbox=(props)=>{
    return(
  <input onchange={props.change} className={props.class} placeholder={props.caption}/>  
    );
}
const [,,,tags]=Appdata;
const Inputag= tags.input_tag.map((n)=>{
    console.log(n);
   return( <Textbox caption={n}  class="input-tag"/>);
})





export default Register;
