import '../App.css';
import React from 'react';
import ReactDOM from 'react-dom';
 import reimg from '../images/reimg.svg';
import Appdata from '../statedata';
 import axios from 'axios';
class Textbox extends React.Component{
    constructor(props){
        super();
        this.state={
          firstname:"",
            email:"",
             surname:""
        }
    }


    Changefirstname=(e)=>{
       this.setState({firstname:e.target.value})

    }

    Changeemail=(e)=>{
       this.setState({email:e.target.value})

    }

    Changesurname=(e)=>{
       this.setState({surname:e.target.value})

    }
    SendToDatabase=async(e)=>{
        e.preventDefault();
        try{
        await  axios({
            method:'post',
              url:'localhost:5000/api/signup',
            data:{
                firstname:this.state.firstname,
                  surname:this.state.surname,
                    email:this.state.email
            }
        })  
        }
        catch(e){
         console.log(e+'cannot be sent, there is an error');   
    }

    }

    render(){
        return(
            <div>
         <input onChange={ this.Changefirstname} className="input-tag" placeholder=" Enter frirstname"/>
             <input onChange={ this.Changesurname} className="input-tag" placeholder=" Enter surname"/>
             <input onChange={this.Changeemail} className="input-tag" placeholder=" Enter email"/>
             <button onClick={this.SendToDatabase} className="i-btn">continue </button>
             </div>
        );
    }
}
export default Textbox;
